mapper.delete:
delete from free_board_vote where board_num=#{board_num};
delete from free_board_reply where board_num=#{board_num};
delete from free_board where board_num=#{board_num};

mapper.update:
update free_board_reply f set
f.reply_re_seq=f.reply_re_seq+1 where
f.reply_re_ref=#{reply_re_ref}
and f.reply_re_seq>#{reply_re_seq};
update free_board_reply f set f.reply_re_ck=f.reply_re_ck+1 where f.reply_re_seq=0 and f.reply_re_ref=#{reply_re_ref};
